package operators;
import java.util.*;
public class While_basic_example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Scanner s=new Scanner(System.in);
System.out.println("enter the limit");
int num=s.nextInt();
int i=0;
while( i<=num)
{
	System.out.println(i);
	i++;
}
	}

}
