
package operators;

public class assignment_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=100;
a+=10; //a=a+10
System.out.println(a);
a-=10;//a=a-10
System.out.println(a);
a*=2;//a=a*2
System.out.println(a);
a/=2;//a=a/2
System.out.println(a);

	}

}
